import{_ as i,o as r,c as a,e as t}from"./app.4c8b15c1.js";const l={},n=t('<h1 id="\u5434\u6069\u8FBE-ai\u521B\u4E1A\u7684\u6838\u5FC3\u8981\u7D20\u4E0E\u672A\u6765\u8D8B\u52BF" tabindex="-1"><a class="header-anchor" href="#\u5434\u6069\u8FBE-ai\u521B\u4E1A\u7684\u6838\u5FC3\u8981\u7D20\u4E0E\u672A\u6765\u8D8B\u52BF" aria-hidden="true">#</a> \u5434\u6069\u8FBE\uFF1AAI\u521B\u4E1A\u7684\u6838\u5FC3\u8981\u7D20\u4E0E\u672A\u6765\u8D8B\u52BF</h1><blockquote><p>YC AI Startup School \u6F14\u8BB2\u8981\u70B9\u6574\u7406</p></blockquote><h2 id="\u6838\u5FC3\u89C2\u70B9\u6982\u8FF0" tabindex="-1"><a class="header-anchor" href="#\u6838\u5FC3\u89C2\u70B9\u6982\u8FF0" aria-hidden="true">#</a> \u6838\u5FC3\u89C2\u70B9\u6982\u8FF0</h2><p>\u8457\u540D\u4EBA\u5DE5\u667A\u80FD\u79D1\u5B66\u5BB6\u3001AI Fund\u521B\u59CB\u4EBA\u5434\u6069\u8FBE\u5728YC AI Startup School\u7684\u6F14\u8BB2\u4E2D\uFF0C\u660E\u786E\u6307\u51FA<strong>AI\u6700\u5927\u7684\u673A\u4F1A\u5B58\u5728\u4E8E\u5E94\u7528\u5C42</strong>\uFF0C\u800C\u975E\u5907\u53D7\u8FFD\u6367\u7684\u57FA\u7840\u6A21\u578B\u5C42\u3002\u4ED6\u6DF1\u5165\u5256\u6790\u4E86AI\u6D6A\u6F6E\u4E0B\u7684\u521B\u4E1A\u8981\u7D20\uFF0C\u63A2\u8BA8\u4E86Agentic AI\u7684\u91CD\u8981\u6027\uFF0C\u5E76\u5F3A\u70C8\u53CD\u9A73\u4E86&quot;AI\u5C06\u4F7F\u7F16\u7A0B\u8FC7\u65F6&quot;\u7684\u8BBA\u8C03\u3002</p><h2 id="\u4E00\u3001ai\u6280\u672F\u6808\u5206\u6790-\u5E94\u7528\u5C42\u7684\u4EF7\u503C\u4F18\u52BF" tabindex="-1"><a class="header-anchor" href="#\u4E00\u3001ai\u6280\u672F\u6808\u5206\u6790-\u5E94\u7528\u5C42\u7684\u4EF7\u503C\u4F18\u52BF" aria-hidden="true">#</a> \u4E00\u3001AI\u6280\u672F\u6808\u5206\u6790\uFF1A\u5E94\u7528\u5C42\u7684\u4EF7\u503C\u4F18\u52BF</h2><h3 id="\u6280\u672F\u6808\u5C42\u7EA7\u7ED3\u6784" tabindex="-1"><a class="header-anchor" href="#\u6280\u672F\u6808\u5C42\u7EA7\u7ED3\u6784" aria-hidden="true">#</a> \u6280\u672F\u6808\u5C42\u7EA7\u7ED3\u6784</h3><p>\u5434\u6069\u8FBE\u63CF\u8FF0\u7684AI\u6280\u672F\u6808\u81EA\u4E0B\u800C\u4E0A\u5305\u62EC\uFF1A</p><ul><li><strong>\u5E95\u5C42</strong>\uFF1A\u534A\u5BFC\u4F53\u516C\u53F8</li><li><strong>\u4E2D\u5C42</strong>\uFF1A\u4E91\u670D\u52A1\u6216\u8D85\u5927\u89C4\u6A21\u8BA1\u7B97\u5E73\u53F0</li><li><strong>\u4E0A\u5C42</strong>\uFF1AAI\u57FA\u7840\u6A21\u578B\u516C\u53F8</li><li><strong>\u9876\u5C42</strong>\uFF1A\u5E94\u7528\u5C42</li></ul><h3 id="\u4E3A\u4EC0\u4E48\u5E94\u7528\u5C42\u6700\u91CD\u8981" tabindex="-1"><a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u5E94\u7528\u5C42\u6700\u91CD\u8981" aria-hidden="true">#</a> \u4E3A\u4EC0\u4E48\u5E94\u7528\u5C42\u6700\u91CD\u8981\uFF1F</h3><p><strong>\u6838\u5FC3\u903B\u8F91</strong>\uFF1A\u53EA\u6709\u5E94\u7528\u5C42\u80FD\u591F\u521B\u9020\u8DB3\u591F\u7684\u6536\u5165\uFF0C\u624D\u80FD\u652F\u4ED8\u57FA\u7840\u6A21\u578B\u3001\u4E91\u670D\u52A1\u548C\u534A\u5BFC\u4F53\u7B49\u6280\u672F\u5C42\u7684\u5F00\u9500\u3002</p><p><strong>\u5EFA\u8BAE</strong>\uFF1A\u6709\u5FD7\u4E8EAI\u521B\u4E1A\u7684\u4EBA\u5E94\u5C06\u76EE\u5149\u805A\u7126\u5728\u5E94\u7528\u5C42\uFF0C\u8FD9\u91CC\u8574\u85CF\u7740\u6700\u5927\u7684\u5546\u4E1A\u673A\u4F1A\u3002</p><h2 id="\u4E8C\u3001agentic-ai-\u6539\u53D8\u6E38\u620F\u89C4\u5219\u7684\u6280\u672F\u8D8B\u52BF" tabindex="-1"><a class="header-anchor" href="#\u4E8C\u3001agentic-ai-\u6539\u53D8\u6E38\u620F\u89C4\u5219\u7684\u6280\u672F\u8D8B\u52BF" aria-hidden="true">#</a> \u4E8C\u3001Agentic AI\uFF1A\u6539\u53D8\u6E38\u620F\u89C4\u5219\u7684\u6280\u672F\u8D8B\u52BF</h2><h3 id="\u4F20\u7EDFllm\u4F7F\u7528\u65B9\u5F0F\u7684\u5C40\u9650" tabindex="-1"><a class="header-anchor" href="#\u4F20\u7EDFllm\u4F7F\u7528\u65B9\u5F0F\u7684\u5C40\u9650" aria-hidden="true">#</a> \u4F20\u7EDFLLM\u4F7F\u7528\u65B9\u5F0F\u7684\u5C40\u9650</h3><ul><li><strong>\u73B0\u72B6\u95EE\u9898</strong>\uFF1A\u5927\u591A\u6570\u4EBA\u4F7F\u7528\u5927\u8BED\u8A00\u6A21\u578B\u7684\u65B9\u5F0F\u662F\u7ED9\u51FA\u63D0\u793A\uFF0C\u7136\u540E\u751F\u6210\u8F93\u51FA</li><li><strong>\u7C7B\u6BD4\u8BF4\u660E</strong>\uFF1A\u8FD9\u5C31\u50CF\u8BA9\u4EBA\u4ECE\u5934\u5230\u5C3E\u4E00\u6C14\u5475\u6210\u5730\u5199\u6587\u7AE0\uFF0C\u671F\u95F4\u4E0D\u80FD\u4F7F\u7528\u9000\u683C\u952E</li><li><strong>\u7ED3\u679C</strong>\uFF1A\u4EBA\u7C7B\u548CAI\u90FD\u65E0\u6CD5\u9002\u5E94\u8FD9\u79CD\u7EBF\u6027\u5DE5\u4F5C\u65B9\u5F0F</li></ul><h3 id="agentic-workflow\u7684\u4F18\u52BF" tabindex="-1"><a class="header-anchor" href="#agentic-workflow\u7684\u4F18\u52BF" aria-hidden="true">#</a> Agentic Workflow\u7684\u4F18\u52BF</h3><p><strong>\u5DE5\u4F5C\u6D41\u7A0B</strong>\uFF1A</p><ol><li>\u5148\u5199\u51FA\u6587\u7AE0\u5927\u7EB2</li><li>\u6839\u636E\u9700\u8981\u8FDB\u884C\u7F51\u7EDC\u641C\u7D22</li><li>\u83B7\u53D6\u76F8\u5173\u7F51\u9875\u4F5C\u4E3A\u53C2\u8003</li><li>\u64B0\u5199\u521D\u7A3F</li><li>\u9605\u8BFB\u3001\u5BA1\u89C6\u5E76\u4FEE\u6539\u521D\u7A3F</li><li>\u5982\u6B64\u5F80\u590D\uFF0C\u5F62\u6210\u8FED\u4EE3\u5FAA\u73AF</li></ol><p><strong>\u6838\u5FC3\u7279\u70B9</strong>\uFF1A</p><ul><li>\u5141\u8BB8AI\u4EE5\u66F4\u590D\u6742\u3001\u66F4\u8FED\u4EE3\u7684\u65B9\u5F0F\u5B8C\u6210\u4EFB\u52A1</li><li>\u6A21\u62DF\u4EBA\u7C7B\u4E13\u5BB6\u7684\u601D\u8003\u8FC7\u7A0B</li><li>\u867D\u7136\u901F\u5EA6\u8F83\u6162\uFF0C\u4F46\u6700\u7EC8\u4EA7\u51FA\u8D28\u91CF\u66F4\u9AD8</li></ul><h3 id="\u5B9E\u8DF5\u9A8C\u8BC1" tabindex="-1"><a class="header-anchor" href="#\u5B9E\u8DF5\u9A8C\u8BC1" aria-hidden="true">#</a> \u5B9E\u8DF5\u9A8C\u8BC1</h3><p>\u5728AI Fund\u7684\u5B9E\u8DF5\u4E2D\uFF0C\u8FD9\u79CD\u5DE5\u4F5C\u6D41\u5728\u4EE5\u4E0B\u573A\u666F\u4E2D\u88AB\u53CD\u590D\u9A8C\u8BC1\uFF1A</p><ul><li>\u586B\u5199\u590D\u6742\u5408\u89C4\u6587\u4EF6</li><li>\u8FDB\u884C\u533B\u7597\u8BCA\u65AD</li><li>\u5BF9\u590D\u6742\u6CD5\u5F8B\u6587\u4EF6\u8FDB\u884C\u63A8\u7406</li></ul><h2 id="\u4E09\u3001\u83B7\u5F97\u901F\u5EA6\u4F18\u52BF\u7684\u6838\u5FC3\u7B56\u7565" tabindex="-1"><a class="header-anchor" href="#\u4E09\u3001\u83B7\u5F97\u901F\u5EA6\u4F18\u52BF\u7684\u6838\u5FC3\u7B56\u7565" aria-hidden="true">#</a> \u4E09\u3001\u83B7\u5F97\u901F\u5EA6\u4F18\u52BF\u7684\u6838\u5FC3\u7B56\u7565</h2><h3 id="_1-\u4E13\u6CE8\u4E8E\u5177\u4F53\u60F3\u6CD5" tabindex="-1"><a class="header-anchor" href="#_1-\u4E13\u6CE8\u4E8E\u5177\u4F53\u60F3\u6CD5" aria-hidden="true">#</a> 1. \u4E13\u6CE8\u4E8E\u5177\u4F53\u60F3\u6CD5</h3><p><strong>&quot;\u5177\u4F53&quot;\u7684\u5B9A\u4E49</strong>\uFF1A\u4E00\u4E2A\u60F3\u6CD5\u5FC5\u987B\u5177\u4F53\u5230\u5DE5\u7A0B\u5E08\u53EF\u4EE5\u76F4\u63A5\u4E0A\u624B\u5F00\u53D1\u7684\u7A0B\u5EA6\u3002</p><p><strong>\u9519\u8BEF\u793A\u4F8B</strong>\uFF1A</p><blockquote><p>&quot;\u8BA9\u6211\u4EEC\u7528AI\u4F18\u5316\u533B\u7597\u4FDD\u5065\u8D44\u4EA7&quot;</p></blockquote><ul><li>\u95EE\u9898\uFF1A\u592A\u6A21\u7CCA\uFF0C\u4E0D\u540C\u5DE5\u7A0B\u5E08\u4F1A\u505A\u51FA\u5B8C\u5168\u4E0D\u540C\u7684\u4EA7\u54C1</li></ul><p><strong>\u6B63\u786E\u793A\u4F8B</strong>\uFF1A</p><blockquote><p>&quot;\u5F00\u53D1\u8F6F\u4EF6\u8BA9\u533B\u9662\u75C5\u4EBA\u5728\u7EBF\u9884\u8BA2\u6838\u78C1\u5171\u632F\u65F6\u6BB5\uFF0C\u4F18\u5316\u8BBE\u5907\u4F7F\u7528\u7387&quot;</p></blockquote><ul><li>\u4F18\u52BF\uFF1A\u5177\u4F53\u660E\u786E\uFF0C\u5DE5\u7A0B\u5E08\u53EF\u4EE5\u5FEB\u901F\u5B9E\u73B0</li></ul><p><strong>\u8BA4\u77E5\u9677\u9631</strong>\uFF1A\u6A21\u7CCA\u7684\u60F3\u6CD5\u66F4\u5BB9\u6613\u83B7\u5F97\u4ED6\u4EBA\u8BA4\u540C\uFF0C\u4F46\u65E0\u6CD5\u5FEB\u901F\u9A8C\u8BC1\u548C\u6267\u884C\u3002</p><h3 id="_2-\u4F9D\u9760\u4E13\u5BB6\u76F4\u89C9\u800C\u975E\u6570\u636E\u5206\u6790" tabindex="-1"><a class="header-anchor" href="#_2-\u4F9D\u9760\u4E13\u5BB6\u76F4\u89C9\u800C\u975E\u6570\u636E\u5206\u6790" aria-hidden="true">#</a> 2. \u4F9D\u9760\u4E13\u5BB6\u76F4\u89C9\u800C\u975E\u6570\u636E\u5206\u6790</h3><p><strong>\u6838\u5FC3\u89C2\u70B9</strong>\uFF1A\u5BF9\u4E8E\u65E9\u671F\u521D\u521B\u516C\u53F8\uFF0C\u4E13\u5BB6\u76F4\u89C9\u5728\u51B3\u7B56\u901F\u5EA6\u4E0A\u5F80\u5F80\u4F18\u4E8E\u6570\u636E\u5206\u6790\u3002</p><p><strong>\u5B9E\u73B0\u8DEF\u5F84</strong>\uFF1A</p><ul><li>\u5BF9\u7279\u5B9A\u95EE\u9898\u8FDB\u884C\u957F\u671F\u6DF1\u5165\u601D\u8003</li><li>\u4E0E\u7528\u6237\u6301\u7EED\u4EA4\u6D41</li><li>\u57F9\u517B\u5BF9\u4F18\u79C0\u4EA7\u54C1\u7684\u76F4\u89C9\u5224\u65AD</li></ul><p><strong>\u6267\u884C\u5EFA\u8BAE</strong>\uFF1A\u5728\u4EFB\u4F55\u65F6\u95F4\u70B9\u90FD\u5E94\u53EA\u8FFD\u6C42\u4E00\u4E2A\u6E05\u6670\u5047\u8BBE\uFF0C\u96C6\u4E2D\u6240\u6709\u8D44\u6E90\u5168\u529B\u9A8C\u8BC1\u3002</p><h3 id="_3-\u6784\u5EFA\u4E0E\u53CD\u9988\u7684\u5FEB\u901F\u5FAA\u73AF" tabindex="-1"><a class="header-anchor" href="#_3-\u6784\u5EFA\u4E0E\u53CD\u9988\u7684\u5FEB\u901F\u5FAA\u73AF" aria-hidden="true">#</a> 3. \u6784\u5EFA\u4E0E\u53CD\u9988\u7684\u5FEB\u901F\u5FAA\u73AF</h3><p><strong>AI\u7F16\u7A0B\u52A9\u7406\u7684\u5F71\u54CD</strong>\uFF1A</p><ul><li>\u5BF9\u751F\u4EA7\u7EA7\u8F6F\u4EF6\u7684\u6548\u7387\u63D0\u5347\uFF1A30-50%</li><li>\u5BF9\u9A8C\u8BC1\u60F3\u6CD5\u7684\u5FEB\u901F\u539F\u578B\u63D0\u5347\uFF1A<strong>10\u500D\u4EE5\u4E0A</strong></li></ul><p><strong>\u7B56\u7565\u5EFA\u8BAE</strong>\uFF1A</p><ul><li>\u9F13\u52B1\u7F16\u5199&quot;\u4E0D\u5B89\u5168&quot;\u7684\u9A8C\u8BC1\u4EE3\u7801\uFF08\u4EC5\u7528\u4E8E\u81EA\u6D4B\uFF09</li><li>\u7CFB\u7EDF\u6027\u521B\u65B0\uFF1A\u4E00\u6B21\u6027\u6784\u5EFA20\u4E2A\u539F\u578B\uFF0C\u7B5B\u9009\u6210\u529F\u8005</li><li>\u5C06\u6982\u5FF5\u9A8C\u8BC1\u6210\u672C\u964D\u5230\u8DB3\u591F\u4F4E</li></ul><h3 id="_4-\u91CD\u65B0\u5B9A\u4E49\u4EE3\u7801\u4EF7\u503C" tabindex="-1"><a class="header-anchor" href="#_4-\u91CD\u65B0\u5B9A\u4E49\u4EE3\u7801\u4EF7\u503C" aria-hidden="true">#</a> 4. \u91CD\u65B0\u5B9A\u4E49\u4EE3\u7801\u4EF7\u503C</h3><p><strong>\u89C2\u5FF5\u8F6C\u53D8</strong>\uFF1A\u968F\u7740\u8F6F\u4EF6\u5DE5\u7A0B\u6210\u672C\u4E0B\u964D\uFF0C\u4EE3\u7801\u4EF7\u503C\u8FDC\u4E0D\u5982\u4ECE\u524D\u3002</p><p><strong>\u5B9E\u8DF5\u4F53\u73B0</strong>\uFF1A</p><ul><li>\u67D0\u56E2\u961F\u4E00\u4E2A\u6708\u5185\u5C06\u4EE3\u7801\u5E93\u5B8C\u5168\u91CD\u6784\u4E09\u6B21</li><li>\u6280\u672F\u67B6\u6784\u9009\u62E9\u4ECE&quot;\u5355\u5411\u95E8&quot;\u53D8\u4E3A&quot;\u53CC\u5411\u95E8&quot;</li><li>\u66F4\u9891\u7E41\u5730\u53D8\u66F4\u6280\u672F\u6808\uFF0C\u4ECE\u96F6\u5F00\u59CB\u91CD\u6784</li></ul><h2 id="\u56DB\u3001\u7F16\u7A0B\u5B66\u4E60\u7684\u91CD\u8981\u6027-\u53CD\u9A73-ai\u8FC7\u65F6\u8BBA" tabindex="-1"><a class="header-anchor" href="#\u56DB\u3001\u7F16\u7A0B\u5B66\u4E60\u7684\u91CD\u8981\u6027-\u53CD\u9A73-ai\u8FC7\u65F6\u8BBA" aria-hidden="true">#</a> \u56DB\u3001\u7F16\u7A0B\u5B66\u4E60\u7684\u91CD\u8981\u6027\uFF1A\u53CD\u9A73&quot;AI\u8FC7\u65F6\u8BBA&quot;</h2><h3 id="\u6838\u5FC3\u8BBA\u65AD" tabindex="-1"><a class="header-anchor" href="#\u6838\u5FC3\u8BBA\u65AD" aria-hidden="true">#</a> \u6838\u5FC3\u8BBA\u65AD</h3><p><strong>\u575A\u51B3\u53CD\u5BF9</strong>\uFF1A&quot;\u56E0\u4E3AAI\u4F1A\u81EA\u52A8\u7F16\u7A0B\uFF0C\u6240\u4EE5\u4EBA\u4EEC\u4E0D\u5E94\u8BE5\u518D\u5B66\u7F16\u7A0B&quot;\u7684\u89C2\u70B9\u3002</p><p><strong>\u5224\u65AD\u4F9D\u636E</strong>\uFF1A\u8FD9\u53EF\u80FD\u662F\u6709\u53F2\u4EE5\u6765\u6700\u7CDF\u7CD5\u7684\u804C\u4E1A\u5EFA\u8BAE\u4E4B\u4E00\u3002</p><h3 id="\u5386\u53F2\u903B\u8F91\u652F\u6491" tabindex="-1"><a class="header-anchor" href="#\u5386\u53F2\u903B\u8F91\u652F\u6491" aria-hidden="true">#</a> \u5386\u53F2\u903B\u8F91\u652F\u6491</h3><p><strong>\u6280\u672F\u53D1\u5C55\u89C4\u5F8B</strong>\uFF1A\u5DE5\u5177\u7684\u8FDB\u6B65\u603B\u662F\u8BA9\u66F4\u591A\u4EBA\u638C\u63E1\u4E00\u9879\u6280\u80FD\uFF0C\u800C\u975E\u66F4\u5C11\u3002</p><p><strong>\u5386\u53F2\u6848\u4F8B</strong>\uFF1A</p><ul><li>\u4ECE\u6253\u5B54\u5361\u5230\u952E\u76D8\u7EC8\u7AEF\uFF1A\u7F16\u7A0B\u53D8\u5F97\u66F4\u5BB9\u6613</li><li>\u4ECE\u6C47\u7F16\u8BED\u8A00\u5230COBOL\uFF1A\u964D\u4F4E\u4E86\u7F16\u7A0B\u95E8\u69DB</li><li>\u6BCF\u6B21\u5DE5\u5177\u9769\u65B0\u90FD\u6269\u5927\u4E86\u4F7F\u7528\u7FA4\u4F53</li></ul><h3 id="\u5168\u5458\u7F16\u7A0B\u7684\u5B9E\u8DF5" tabindex="-1"><a class="header-anchor" href="#\u5168\u5458\u7F16\u7A0B\u7684\u5B9E\u8DF5" aria-hidden="true">#</a> \u5168\u5458\u7F16\u7A0B\u7684\u5B9E\u8DF5</h3><p><strong>\u56E2\u961F\u5B9E\u4F8B</strong>\uFF1A\u5434\u6069\u8FBE\u56E2\u961F\u4E2D\u7684\u5168\u5458\u7F16\u7A0B\u5B9E\u8DF5</p><ul><li>\u9996\u5E2D\u8D22\u52A1\u5B98\u3001\u4EBA\u624D\u4E3B\u7BA1\u3001\u62DB\u8058\u4E13\u5458\u3001\u524D\u53F0\u90FD\u4F1A\u7F16\u7A0B</li><li>\u7F16\u7A0B\u80FD\u529B\u8BA9\u4ED6\u4EEC\u5728\u5404\u81EA\u5C97\u4F4D\u4E0A\u8868\u73B0\u66F4\u51FA\u8272</li></ul><p><strong>\u6838\u5FC3\u6280\u80FD</strong>\uFF1A\u672A\u6765\u6700\u91CD\u8981\u7684\u6280\u80FD\u662F\u80FD\u591F\u6E05\u6670\u51C6\u786E\u5730\u544A\u8BC9\u8BA1\u7B97\u673A\u4F60\u60F3\u8981\u4EC0\u4E48\uFF0C\u5E76\u8BA9\u5B83\u6267\u884C\u3002</p><p><strong>\u5B66\u4E60\u76EE\u6807</strong>\uFF1A\u4E0D\u662F\u4EB2\u81EA\u7F16\u5199\u6BCF\u4E00\u884C\u4EE3\u7801\uFF0C\u800C\u662F\u5B66\u4F1A\u5F15\u5BFCAI\u4E3A\u4F60\u7F16\u7A0B\u3002</p><h2 id="\u4E94\u3001\u4EA7\u54C1\u7BA1\u7406\u6210\u4E3A\u65B0\u74F6\u9888" tabindex="-1"><a class="header-anchor" href="#\u4E94\u3001\u4EA7\u54C1\u7BA1\u7406\u6210\u4E3A\u65B0\u74F6\u9888" aria-hidden="true">#</a> \u4E94\u3001\u4EA7\u54C1\u7BA1\u7406\u6210\u4E3A\u65B0\u74F6\u9888</h2><h3 id="\u95EE\u9898\u73B0\u8C61" tabindex="-1"><a class="header-anchor" href="#\u95EE\u9898\u73B0\u8C61" aria-hidden="true">#</a> \u95EE\u9898\u73B0\u8C61</h3><p><strong>\u901F\u5EA6\u5012\u6302</strong>\uFF1A\u5DE5\u7A0B\u5F00\u53D1\u901F\u5EA6\u63D0\u5347\u5BFC\u81F4\u4EA7\u54C1\u7BA1\u7406\u6210\u4E3A\u74F6\u9888</p><ul><li>\u83B7\u53D6\u7528\u6237\u53CD\u9988</li><li>\u51B3\u7B56\u5F00\u53D1\u529F\u80FD</li><li>\u4EA7\u54C1\u89C4\u5212\u548C\u8FED\u4EE3</li></ul><h3 id="\u89E3\u51B3\u65B9\u6848" tabindex="-1"><a class="header-anchor" href="#\u89E3\u51B3\u65B9\u6848" aria-hidden="true">#</a> \u89E3\u51B3\u65B9\u6848</h3><p><strong>\u4EBA\u5458\u914D\u6BD4\u8C03\u6574</strong>\uFF1A\u9996\u6B21\u51FA\u73B0\u4EA7\u54C1\u7ECF\u7406\u4E0E\u5DE5\u7A0B\u5E081:0.5\u7684\u914D\u6BD4\u5EFA\u8BAE\uFF08\u6BCF\u4F4D\u5DE5\u7A0B\u5E08\u914D\u5907\u4E24\u540D\u4EA7\u54C1\u7ECF\u7406\uFF09</p><p><strong>\u5FEB\u901F\u53CD\u9988\u673A\u5236</strong>\uFF1A</p><ol><li>\u4F9D\u9760\u4EA7\u54C1\u76F4\u89C9</li><li>\u627E3-5\u4E2A\u670B\u53CB\u8BD5\u7528</li><li>\u627E3-10\u4E2A\u964C\u751F\u4EBA\u83B7\u53D6\u53CD\u9988\uFF08\u6700\u91CD\u8981\u7684\u4EA7\u54C1\u6280\u80FD\u4E4B\u4E00\uFF09</li></ol><p><strong>A/B\u6D4B\u8BD5\u7684\u6DF1\u5C42\u4EF7\u503C</strong>\uFF1A</p><ul><li>\u4E0D\u4EC5\u662F\u5728\u65B9\u6848\u95F4\u9009\u62E9</li><li>\u66F4\u91CD\u8981\u7684\u662F\u901A\u8FC7\u6570\u636E\u6821\u51C6\u76F4\u89C9</li><li>\u5229\u7528\u6570\u636E\u66F4\u65B0\u5FC3\u667A\u6A21\u578B\uFF0C\u63D0\u5347\u51B3\u7B56\u80FD\u529B</li></ul><h2 id="\u516D\u3001ai\u6280\u672F\u7406\u89E3\u7684\u7ADE\u4E89\u4F18\u52BF" tabindex="-1"><a class="header-anchor" href="#\u516D\u3001ai\u6280\u672F\u7406\u89E3\u7684\u7ADE\u4E89\u4F18\u52BF" aria-hidden="true">#</a> \u516D\u3001AI\u6280\u672F\u7406\u89E3\u7684\u7ADE\u4E89\u4F18\u52BF</h2><h3 id="\u6280\u672F\u5224\u65AD\u7684\u91CD\u8981\u6027" tabindex="-1"><a class="header-anchor" href="#\u6280\u672F\u5224\u65AD\u7684\u91CD\u8981\u6027" aria-hidden="true">#</a> \u6280\u672F\u5224\u65AD\u7684\u91CD\u8981\u6027</h3><p><strong>\u73B0\u72B6\u5206\u6790</strong>\uFF1A</p><ul><li>\u6210\u719F\u6280\u672F\uFF08\u624B\u673A\u5E94\u7528\u3001\u9500\u552E\u3001\u5E02\u573A\u7B49\uFF09\u77E5\u8BC6\u5DF2\u666E\u53CA</li><li>AI\u4F5C\u4E3A\u65B0\u5174\u6280\u672F\uFF0C\u76F8\u5173\u77E5\u8BC6\u8FDC\u672A\u666E\u53CA</li><li>\u771F\u6B63&quot;\u61C2&quot;AI\u7684\u56E2\u961F\u62E5\u6709\u663E\u8457\u4F18\u52BF</li></ul><p><strong>\u5173\u952E\u51B3\u7B56\u793A\u4F8B</strong>\uFF1A</p><ul><li>\u5BA2\u670D\u804A\u5929\u673A\u5668\u4EBA\u7684\u51C6\u786E\u7387\u4E0A\u9650</li><li>\u9009\u62E9\u63D0\u793A\u8BCD\u5DE5\u7A0Bvs\u6A21\u578B\u5FAE\u8C03vs AI Agent\u5DE5\u4F5C\u6D41</li><li>\u5B9E\u73B0\u4F4E\u5EF6\u8FDF\u8BED\u97F3\u8F93\u51FA\u7684\u65B9\u6848</li></ul><p><strong>\u5F71\u54CD\u7A0B\u5EA6</strong>\uFF1A\u6B63\u786E\u7684\u6280\u672F\u9009\u62E9\u53EF\u80FD\u8BA9\u4F60\u51E0\u5929\u5185\u89E3\u51B3\u95EE\u9898\uFF0C\u9519\u8BEF\u9009\u62E9\u53EF\u80FD\u8BA9\u901F\u5EA6\u6162\u5341\u500D\u3002</p><h3 id="\u4E30\u5BCC\u7684\u6784\u5EFA\u6A21\u5757" tabindex="-1"><a class="header-anchor" href="#\u4E30\u5BCC\u7684\u6784\u5EFA\u6A21\u5757" aria-hidden="true">#</a> \u4E30\u5BCC\u7684\u6784\u5EFA\u6A21\u5757</h3><p><strong>\u53EF\u7528\u5DE5\u5177</strong>\uFF1A\u63D0\u793A\u8BCD\u5DE5\u7A0B\u3001AI Agent\u5DE5\u4F5C\u6D41\u3001\u8BC4\u4F30\u3001\u62A4\u680F\u3001RAG\u3001\u8BED\u97F3\u4F18\u5148\u3001\u5F02\u6B65\u7F16\u7A0B\u3001\u5FAE\u8C03\u3001\u56FE\u6570\u636E\u5E93\u7B49</p><p><strong>\u521B\u65B0\u673A\u9047</strong>\uFF1A\u901A\u8FC7\u5FEB\u901F\u7EC4\u5408\u8FD9\u4E9B&quot;\u4E50\u9AD8\u79EF\u6728&quot;\uFF0C\u80FD\u591F\u521B\u9020\u51FA\u4E00\u5E74\u524D\u5730\u7403\u4E0A\u4EFB\u4F55\u4EBA\u90FD\u65E0\u6CD5\u6784\u5EFA\u7684\u8F6F\u4EF6\u3002</p><h2 id="\u4E03\u3001\u95EE\u7B54\u73AF\u8282-\u7834\u9664\u7092\u4F5C\u4E0E\u5C55\u671B\u672A\u6765" tabindex="-1"><a class="header-anchor" href="#\u4E03\u3001\u95EE\u7B54\u73AF\u8282-\u7834\u9664\u7092\u4F5C\u4E0E\u5C55\u671B\u672A\u6765" aria-hidden="true">#</a> \u4E03\u3001\u95EE\u7B54\u73AF\u8282\uFF1A\u7834\u9664\u7092\u4F5C\u4E0E\u5C55\u671B\u672A\u6765</h2><h3 id="\u7834\u9664ai\u7092\u4F5C\u7684\u5224\u65AD\u6846\u67B6" tabindex="-1"><a class="header-anchor" href="#\u7834\u9664ai\u7092\u4F5C\u7684\u5224\u65AD\u6846\u67B6" aria-hidden="true">#</a> \u7834\u9664AI\u7092\u4F5C\u7684\u5224\u65AD\u6846\u67B6</h3><p><strong>\u6838\u5FC3\u539F\u5219</strong>\uFF1A\u8B66\u60D5\u90A3\u4E9B\u8BA9\u67D0\u4E9B\u4F01\u4E1A\u663E\u5F97\u6BD4\u5B9E\u9645\u66F4\u5F3A\u5927\u7684\u7092\u4F5C\u53D9\u4E8B\u3002</p><p><strong>\u5177\u4F53\u7092\u4F5C\u8BC6\u522B</strong>\uFF1A</p><ul><li><strong>&quot;AI\u672B\u65E5\u8BBA&quot;</strong>\uFF1A\u670D\u52A1\u4E8E\u4F01\u4E1A\u878D\u8D44\u548C\u516C\u5173\u7684\u7092\u4F5C</li><li><strong>&quot;AI\u5BFC\u81F4\u5931\u4E1A&quot;</strong>\uFF1A\u8BA9\u4F01\u4E1A\u663E\u5F97\u66F4\u5F3A\u5927\u7684\u8C23\u8A00</li><li><strong>&quot;\u5927\u6A21\u578B\u627C\u6740\u521D\u521B\u516C\u53F8&quot;</strong>\uFF1A\u5938\u5927\u4E86\u5927\u516C\u53F8\u7684\u5F71\u54CD\u529B</li></ul><h3 id="agi\u6982\u5FF5\u7684\u7406\u6027\u770B\u5F85" tabindex="-1"><a class="header-anchor" href="#agi\u6982\u5FF5\u7684\u7406\u6027\u770B\u5F85" aria-hidden="true">#</a> AGI\u6982\u5FF5\u7684\u7406\u6027\u770B\u5F85</h3><p><strong>\u57FA\u672C\u5224\u65AD</strong>\uFF1AAGI\u6982\u5FF5\u88AB\u8FC7\u5EA6\u7092\u4F5C\uFF0C\u672A\u6765\u5F88\u957F\u65F6\u95F4\u5185\u4EBA\u7C7B\u4EF7\u503C\u4E0D\u53EF\u66FF\u4EE3\u3002</p><p><strong>\u6838\u5FC3\u89C2\u70B9</strong>\uFF1A</p><ul><li>\u4ECD\u6709\u5927\u91CF\u5DE5\u4F5C\u662F\u4EBA\u7C7B\u80FD\u505A\u800CAI\u505A\u4E0D\u4E86\u7684</li><li>\u6700\u5F3A\u5927\u7684\u4EBA\u662F\u90A3\u4E9B\u80FD\u9A71\u4F7F\u8BA1\u7B97\u673A\u7CBE\u51C6\u6267\u884C\u610F\u56FE\u7684\u4EBA</li><li>\u5584\u7528AI\u7684\u4EBA\u5C06\u6BD4\u4E0D\u4F7F\u7528AI\u7684\u4EBA\u5F3A\u5927\u5F97\u591A</li></ul><h3 id="ai-agent\u7D2F\u79EF\u6548\u5E94\u7684\u6280\u672F\u5EFA\u8BAE" tabindex="-1"><a class="header-anchor" href="#ai-agent\u7D2F\u79EF\u6548\u5E94\u7684\u6280\u672F\u5EFA\u8BAE" aria-hidden="true">#</a> AI Agent\u7D2F\u79EF\u6548\u5E94\u7684\u6280\u672F\u5EFA\u8BAE</h3><p><strong>\u5F00\u53D1\u5EFA\u8BAE</strong>\uFF1A</p><ol><li>\u521D\u671F\u57FA\u672C\u4E0D\u8981\u62C5\u5FC3Token\u6210\u672C</li><li>\u8BBE\u8BA1\u7075\u6D3B\u7684\u8F6F\u4EF6\u67B6\u6784\uFF0C\u4FBF\u4E8E\u5728\u4E0D\u540C\u57FA\u7840\u6A21\u5757\u63D0\u4F9B\u5546\u95F4\u5207\u6362</li><li>\u6574\u5408\u63D0\u793A\u5DE5\u7A0B\u3001RAG\u3001\u8BC4\u4F30\u4F53\u7CFB\u7B49\u591A\u4E2A\u6B65\u9AA4</li></ol><h3 id="\u77E5\u8BC6\u666E\u53CA\u4E0E\u5F00\u6E90\u4FDD\u62A4" tabindex="-1"><a class="header-anchor" href="#\u77E5\u8BC6\u666E\u53CA\u4E0E\u5F00\u6E90\u4FDD\u62A4" aria-hidden="true">#</a> \u77E5\u8BC6\u666E\u53CA\u4E0E\u5F00\u6E90\u4FDD\u62A4</h3><p><strong>\u666E\u53CA\u524D\u666F</strong>\uFF1A\u77E5\u8BC6\u7EC8\u5C06\u666E\u53CA\uFF0C\u8FD9\u662F\u6559\u80B2\u7684\u4F7F\u547D\u3002</p><p><strong>\u4E3B\u8981\u98CE\u9669</strong>\uFF1A</p><ol><li><strong>\u666E\u53CA\u901F\u5EA6\u4E0D\u591F</strong>\uFF1A\u6CA1\u80FD\u8BA9\u6240\u6709\u4EBA\u53CA\u65F6\u8DDF\u4E0A\u6B65\u4F10</li><li><strong>\u521B\u65B0\u627C\u6740</strong>\uFF1A\u67D0\u4E9B\u516C\u53F8\u5229\u7528\u5938\u5927\u7684&quot;AI\u98CE\u9669&quot;\u6E38\u8BF4\u76D1\u7BA1\uFF0C\u8BD5\u56FE\u6210\u4E3A\u57FA\u7840\u6A21\u578B&quot;\u5B88\u95E8\u4EBA&quot;</li></ol><p><strong>\u884C\u52A8\u547C\u5401</strong>\uFF1A\u5FC5\u987B\u4FDD\u62A4\u5F00\u6E90\u548C\u5F00\u653E\u6743\u91CD\u6A21\u578B\uFF0C\u963B\u6B62\u5BF9\u521B\u65B0\u7684\u627C\u6740\u3002</p><h2 id="\u6838\u5FC3\u7ED3\u8BBA" tabindex="-1"><a class="header-anchor" href="#\u6838\u5FC3\u7ED3\u8BBA" aria-hidden="true">#</a> \u6838\u5FC3\u7ED3\u8BBA</h2><ol><li><strong>\u5E94\u7528\u5C42\u4EF7\u503C\u6700\u5927</strong>\uFF1AAI\u521B\u4E1A\u7684\u673A\u4F1A\u5728\u5E94\u7528\u5C42\uFF0C\u4E0D\u5728\u57FA\u7840\u6A21\u578B\u5C42</li><li><strong>Agentic AI\u662F\u8D8B\u52BF</strong>\uFF1A\u8FED\u4EE3\u5F0F\u5DE5\u4F5C\u6D41\u5C06\u6210\u4E3AAI\u5E94\u7528\u7684\u4E3B\u6D41\u6A21\u5F0F</li><li><strong>\u901F\u5EA6\u662F\u5173\u952E\u4F18\u52BF</strong>\uFF1A\u901A\u8FC7\u5177\u4F53\u60F3\u6CD5\u3001\u4E13\u5BB6\u76F4\u89C9\u3001\u5FEB\u901F\u539F\u578B\u5B9E\u73B0\u7ADE\u4E89\u4F18\u52BF</li><li><strong>\u5168\u5458\u7F16\u7A0B\u65F6\u4EE3</strong>\uFF1A\u6BCF\u4E2A\u5C97\u4F4D\u7684\u4EBA\u90FD\u5E94\u8BE5\u5B66\u4E60\u7F16\u7A0B\uFF0C\u638C\u63E1\u4E0EAI\u534F\u4F5C\u7684\u80FD\u529B</li><li><strong>\u6280\u672F\u7406\u89E3\u662F\u58C1\u5792</strong>\uFF1A\u6DF1\u523B\u7406\u89E3AI\u6280\u672F\u5C06\u6210\u4E3A\u56E2\u961F\u7684\u6838\u5FC3\u7ADE\u4E89\u529B</li><li><strong>\u4FDD\u62A4\u5F00\u6E90\u521B\u65B0</strong>\uFF1A\u53CD\u5BF9\u8FC7\u5EA6\u76D1\u7BA1\uFF0C\u4FDD\u62A4\u5F00\u6E90\u793E\u533A\u7684\u521B\u65B0\u6D3B\u529B</li></ol><p>\u8FD9\u573A\u6F14\u8BB2\u4E3AAI\u65F6\u4EE3\u7684\u521B\u4E1A\u8005\u63D0\u4F9B\u4E86\u6E05\u6670\u7684\u65B9\u5411\u6307\u5F15\u548C\u5B9E\u7528\u7684\u6267\u884C\u7B56\u7565\uFF0C\u5F3A\u8C03\u4E86\u5728\u6280\u672F\u5FEB\u901F\u53D1\u5C55\u4E2D\u4FDD\u6301\u7406\u6027\u5224\u65AD\u548C\u5FEB\u901F\u6267\u884C\u7684\u91CD\u8981\u6027\u3002</p>',99),o=[n];function e(s,h){return r(),a("div",null,o)}var g=i(l,[["render",e],["__file","aiRead.html.vue"]]);export{g as default};
